<?php

namespace Civi\SecurityStore\Features\Access\Rol\View;

use Civi\SecurityStore\Features\Access\Rol\Rol;
use Civi\View\ViewMetadata;
use Civi\Store\EntityViewMetadata;
use Civi\Store\Repository;

class RolViewMetadata
{
    private readonly EntityViewMetadata $repository;
    /**
     * @autogenerated
     */
    public function __construct(Repository $repository)
    {
        $this->repository = $repository->formMetadata('usermanagement', Rol::class);
    }
    /**
     * @autogenerated
     */
    public function build(): ViewMetadata
    {
        return $this->repository->build();
    }
}
